tnthai = require('../tnthai')
assert = require('assert')

console.time("init tnthai (trie loading) time")
var analyzer = new tnthai()
console.timeEnd("init tnthai (trie loading) time")

console.time("case 1 segment time")
case1input = "คนแก่ขนของต่างหากใครกล่าวกันว่าคนหนุ่มไปขนของหล่ะ john davis i'm fine, thank you. And you?"
case1 = analyzer.segmenting(case1input, {filterStopword : true})
console.timeEnd("case 1 segment time")
a = JSON.stringify(case1)
b = JSON.stringify({ solution: [ 'คนแก่','ขนของ','ต่างหาก','ใคร','คนหนุ่ม','ขนของ','หล่ะ',' ','john',' ','davis',' ','i','\'','m',' ','fine',',',' ','thank',' ','you','.',' ','And',' ','you','?' ]})
console.log("input : " + case1input)
console.log("output : " + b)
assert.equal(a, b, "object : " + a + " is not equal object : " + b)

console.time("case 2 segment time")
case2input = "เราคนหนึ่งคนนั้น ในวันหนึ่งวันนั้นเรายังผูกพันกันมากมาย"
case2 = analyzer.segmenting(case2input, {filterStopword : true})
console.timeEnd("case 2 segment time")
a = JSON.stringify(case2)
b = JSON.stringify({ solution: [ 'คน', 'คน', ' ', 'วันหนึ่ง', 'ผูกพัน', 'มากมาย' ]})
console.log("input : " + case2input)
console.log("output : " + b)
assert.equal(a, b, "object : " + a + " is not equal object : " + b)

console.time("case 3 segment time")
case3input = "คนแก่ขนของต่างหากใครกล่าวกันว่าคนหนุ่มไปขนของหล่ะ"
case3 = analyzer.segmenting(case3input)
